   // \u4F7F\u7528Join\u67E5\u8BE2\u8BA2\u5355\u8BE6\u60C5\n    orderDetail, err := g.Model("order o").\n        LeftJoin("payment p", "p.order_id = o.id").\n        LeftJoin("user_address ua", "ua.id = o.address_id").\n        Fields(\n            "o.*",\n            "p.payment_method",\n            "p.payment_time",\n            "p.transaction_id",\n            "ua.receiver_name",\n            "ua.receiver_phone",\n            "ua.province",\n            "ua.city",\n            "ua.district",\n            "ua.detail_address",\n        ).\n        Where("o.id", orderId).\n        One()\n    if err != nil {\n        return nil, err\n    }\n    \n    // \u67E5\u8BE2\u8BA2\u5355\u5546\u54C1\n    orderItems, err := g.Model("order_item oi").\n        LeftJoin("product p", "p.id = oi.product_id").\n        Fields(\n            "oi.*",\n            "p.product_name",\n            "p.product_image",\n        ).\n        Where("oi.order_id", orderId).\n        All()\n    if err != nil {\n        return nil, err\n    }\n    \n    // \u7EC4\u5408\u6570\u636E\n    result := orderDetail.Map()\n    result["items"] = orderItems.List()\n    \n    return result, nil\n}\n'})}),"\n",(0,d.jsx)(n.h3,{id:"22-\u4F7F\u7528\u5B50\u67E5\u8BE2\u5B9E\u73B0\u8D2D\u7269\u8F66\u67E5\u8BE2",children:"2.2 \u4F7F\u7528\u5B50\u67E5\u8BE2\u5B9E\u73B0\u8D2D\u7269\u8F66\u67E5\u8BE2"}),"\n",(0,d.jsx)(n.pre,{children:(0,d.jsx)(n.code,{className:"language-go",children:'// \u8D2D\u7269\u8F66\u67E5\u8BE2 - \u4F7F\u7528JOIN\u65B9\u5F0F\u5B9E\u73B0\nfunc GetUserCartWithJoin(ctx context.Context, userId int64) ([]map[string]interface{}, error) {\n    cartItems, err := g.Model("cart c").\n        LeftJoin("product p", "p.id = c.product_id").\n        Fields(\n            "c.*",\n            "p.product_name",\n            "p.product_image",\n            "p.price as current_price",\n            "p.stock as current_stock",\n        ).\n        Where("c.user_id", userId).\n        Order("c.create_time DESC").\n        All()\n    if err != nil {\n        return nil, err\n    }\n    \n    return cartItems.List(), nil\n}\n\n// \u8D2D\u7269\u8F66\u67E5\u8BE2 - \u4F7F\u7528FROM\u5B50\u67E5\u8BE2\u65B9\u5F0F\nfunc GetUserCartWithFromSubQuery(ctx context.Context, userId int64) ([]map[string]interface{}, error) {\n    // \u521B\u5EFA\u8D2D\u7269\u8F66\u5B50\u67E5\u8BE2\n    cartSubQuery := g.Model("cart").\n        Fields("id", "product_id", "quantity", "create_time").\n        Where("user_id", userId)\n    \n    // \u521B\u5EFA\u5546\u54C1\u5B50\u67E5\u8BE2\n    productSubQuery := g.Model("product").\n        Fields("id", "product_name", "product_image", "price", "stock")\n    \n    // \u4F7F\u7528FROM\u5B50\u67E5\u8BE2\u65B9\u5F0F\u5173\u8054\u4E24\u4E2A\u5B50\u67E5\u8BE2\n    result, err := g.Model("? as c, ? as p", cartSubQuery, productSubQuery).\n        Where("c.product_id = p.id").\n        Fields(\n            "c.*",\n            "p.product_name",\n            "p.product_image",\n            "p.price as current_price",\n            "p.stock as current_stock",\n        ).\n        Order("c.create_time DESC").\n        All()\n    if err != nil {\n        return nil, err\n    }\n    \n    return result.List(), nil\n}\n'})}),"\n",(0,d.jsx)(n.h2,{id:"3-\u5927\u6570\u636E\u91CF\u4E0B\u5173\u8054\u8868\u67E5\u8BE2\u7684\u6027\u80FD\u95EE\u9898",children:"3. \u5927\u6570\u636E\u91CF\u4E0B\u5173\u8054\u8868\u67E5\u8BE2\u7684\u6027\u80FD\u95EE\u9898"}),"\n",(0,d.jsx)(n.p,{children:"\u867D\u7136\u5173\u8054\u8868\u67E5\u8BE2\u5728\u67D0\u4E9B\u573A\u666F\u4E0B\u975E\u5E38\u65B9\u4FBF\uFF0C\u4F46\u5728\u5927\u6570\u636E\u91CF\u3001\u9AD8\u5E76\u53D1\u7684\u4E1A\u52A1\u573A\u666F\u4E0B\uFF0C\u5173\u8054\u8868\u67E5\u8BE2\u53EF\u80FD\u4F1A\u5E26\u6765\u4E25\u91CD\u7684\u6027\u80FD\u95EE\u9898\uFF1A"}),"\n",(0,d.jsx)(n.h3,{id:"31-\u6027\u80FD\u74F6\u9888",children:"3.1 \u6027\u80FD\u74F6\u9888"}),"\n",(0,d.jsxs)(n.ol,{children:["\n",(0,d.jsxs)(n.li,{children:["\n",(0,d.jsxs)(n.p,{children:[(0